import json
import psycopg2
import random
import requests

def upload_image(image_url):
    path = f"/usr/src/app/media/uploads/products/{image_url.split('/')[-1]}"
    with open(path, 'wb') as f:
        f.write(requests.get(image_url).content)
    return None

